<?php
function Tels($state)
{
    global $site_setting;
    $tels = $site_setting["tels"];

    $iconClass = "hover-opacity xl:w-[27px] w-9 xl:h-[27px] h-9";

    $isFooter = $state === "footer";
    $isSaleContact = $state === "sale_contact";
    $isExecuteContact = $state === "execute_contact";

    $info["select"] = match (true) {
        $isFooter => "sale",
        $isSaleContact => "sale",
        $isExecuteContact => "execute",
    };

    $containerClass = match (true) {
        $isFooter => "gap-2.5 xl:items-start items-center",
        $isSaleContact || $isExecuteContact => "xl:gap-5 gap-3",
    };

    $itemContainerClass = match (true) {
        $isFooter => "flex-col xl:items-end items-center gap-2.5",
        $isSaleContact || $isExecuteContact => "items-center gap-3",
    };

    $sale_tels = $tels[$info["select"]];
?>
    <ul class="flex flex-col <?= $containerClass; ?>">
        <?php foreach ($sale_tels as $tel) : $tel = esc_html($tel["tel"]); ?>
            <li class="flex <?= $itemContainerClass; ?>">
                <a class="hover:text-main" href="tel:<?= Help::getNumber($tel); ?>">
                    <?= $tel; ?>
                </a>

                <?php if ($isFooter || $isSaleContact) : ?>
                    <ul class="flex xl:gap-2.5 gap-3 items-center flex-shrink-0">
                        <li>
                            <a target="_blank" class="<?= $iconClass; ?>" href="viber://chat?number=%2B<?= Help::getNumber($tel, true); ?>">
